#FUNCTIONS
def find_largest_product(num, adj_dig_count):
    num = str(num)
    int_list = []
    products = []
    for chr in num:
        int_list.append(int(chr))
    
    #loops through all indexes from 0 to the edge
    for i in range(0, (len(int_list) + 1) - adj_dig_count):
        nums = []
        product = 1
        
        #adds the included digits to the nums list (eg. 1, 2, 3; 6, 7, 8)
        for j in range(i, i + adj_dig_count):
            nums.append(int_list[j])
        
        #multiplies the numbers together to find the product
        for num in nums:
            product = product * num
        products.append(product)
        
    return(max(products))


